mapper.select:
		SELECT count(1)
        FROM t_vod_series
        WHERE	1=1
		
               AND SERIES_ID = #{SERIES_ID}
        
		
               AND NAME LIKE #{NAME}
       	
       	
               AND TYPE = #{TYPE}
       	
      	
               AND STATUS = #{STATUS}
       	
      	
               AND CDN_TIME BETWEEN '${CDN_TIME} 00:00:00' AND '${CDN_TIME} 23:59:59'

SELECT s.SERIES_ID, s.ORIGINAL_ID, s.CP_ID, s.EPISODE_TYPE, s.TYPE, s.TYPE_NAME , s.NAME, s.FULLSPELL, s.SIMPLESPELL, s.ALIAS_NAME, s.EN_NAME, s.LANGUAGE , s.CAPTION, s.RELEASE_TIME, s.AREA, s.PROGRAM_COUNT, s.NEW_PROGRAM , s.DIRECTOR, s.PRODUCER, s.ADAPTOR, s.ACTOR, s.AWARDS, s.DESCRIPTION, s.KEYWORDS , s.KIND, s.TAGS, s.CLARITY, s.COPYRIGHT, s.COPYRIGHT_DATE, s.IMPORT_SOURCE , s.STATUS, s.CDN_ID , date_format(s.CREATE_TIME, '%Y-%m-%d %H:%i:%s') CREATE_TIME , date_format(s.CDN_TIME, '%Y-%m-%d %H:%i:%s') CDN_TIME , date_format(s.ONLINE_TIME, '%Y-%m-%d %H:%i:%s') ONLINE_TIME , date_format(s.UNLINE_TIME, '%Y-%m-%d %H:%i:%s') UNLINE_TIME , CASE WHEN s.REAL_PROGRAM_COUNT IS NULL THEN 0 ELSE s.REAL_PROGRAM_COUNT END REAL_PROGRAM_COUNT , CASE WHEN s.ONLINE_PROGRAM_COUNT IS NULL THEN 0 ELSE s.ONLINE_PROGRAM_COUNT END ONLINE_PROGRAM_COUNT , CASE WHEN s.IS_LACK_PROGRAM IS NULL THEN 0 ELSE s.IS_LACK_PROGRAM END IS_LACK_PROGRAM FROM t_vod_series s WHERE 1=1
AND SERIES_ID = #{SERIES_ID}
AND NAME LIKE #{NAME}
AND TYPE = #{TYPE}
AND STATUS = #{STATUS}
AND CDN_TIME BETWEEN '${CDN_TIME} 00:00:00' AND '${CDN_TIME} 23:59:59'
ORDER BY s.CREATE_TIME DESC LIMIT ${start},${limit}
