mapper.select:
select count(ID) 
from t_xml_field
where 1=1

	and NAME LIKE concat('%', #{name}, '%')


	and CODE = #{code}


	and PARENT_ID = #{parentId}

select ID, CODE, NAME, VALUE, PARENT_ID, STATUS, DESCRIPTION, ORDERINDEX from t_xml_field where 1=1
and NAME LIKE concat('%', #{name}, '%')
and CODE = #{code}
and PARENT_ID = #{parentId}
ORDER BY PARENT_ID, ORDERINDEX ASC LIMIT ${start},${limit}

SELECT COUNT(1) FROM t_bas_pubcode 
 WHERE CODE = #{CODE} AND PARENT_ID = #{PARENT_ID}

 	ID != #ID#

        SELECT b.ID, b.CODE, b.NAME, b.STATUS 
		FROM t_bas_pubcode a 
		INNER JOIN t_bas_pubcode b ON a.ID = b.PARENT_ID
		where a.CODE = #{code}
		
			b.STATUS = #{status}
		
		ORDER BY b.ORDERINDEX
